mapper.select:
SELECT DISTINCT TO_CHAR(s.schedule_date, 'yyyy-mm-dd') as sday FROM schedule s
JOIN screen sc ON s.screen_id = sc.screen_id JOIN theater t ON t.theater_id = sc.theater_id JOIN movie m ON m.movie_id = s.movie_id
WHERE s.schedule_date >= SYSDATE
AND m.movie_id = #{movie_id}
AND t.theater_name = #{theater}

SELECT DISTINCT t.theater_name, t.theater_id FROM theater t
JOIN screen sc ON sc.theater_id = t.theater_id JOIN schedule s ON s.screen_id = sc.screen_id JOIN movie m ON m.movie_id = s.movie_id
s.schedule_date >= SYSDATE
AND m.movie_id = #{movie}
AND t.region_no = #{region}
